mapper.update:
UPDATE d_board
SET  views = NVL(views,0)+ 1
WHERE seq = #{seq}
AND user_id <> #{userId}

choose.when:
AND gname = (
    SELECT det_code_nm 
    FROM d_code 
    WHERE mst_code = 'GNAME' 
      AND det_code = #{searchDiv}
)

AND gname = (
    SELECT det_code_nm 
    FROM d_code 
    WHERE mst_code = 'GNAME' 
      AND det_code = #{searchGu}
)

mapper.select:
-- TODO : 커뮤니티(10), 공지사항(20) SELECT A.*, B.* FROM ( SELECT tt1.rnum as no, tt1.div, tt1.gname, tt1.title, tt1.mod_id as modId, DECODE( TO_CHAR(tt1.mod_dt,'YYYYMMDD'),TO_CHAR(SYSDATE,'YYYYMMDD'), TO_CHAR(tt1.mod_dt,'HH24:MI'),TO_CHAR(tt1.mod_dt,'YYYY/MM/DD') ) as modDt, tt1.views, TT1.seq FROM ( SELECT ROWNUM as rnum, T1.* FROM ( SELECT * FROM d_board --WHERE : title(10), content(20), title+content(30) WHERE 1=1
ORDER BY mod_dt DESC )T1 WHERE rownum <= ( #{pageSize} * (#{pageNo} -1) + #{pageSize} ) )TT1 WHERE tt1.rnum >= ( #{pageSize} * (#{pageNo} -1) + 1 ) )A CROSS JOIN ( SELECT COUNT (*) totalCnt FROM d_board WHERE 1=1
)B

mapper.delete:
DELETE FROM d_board
WHERE seq = #{seq}
  AND user_id = #{userId}

DELETE FROM d_board
 WHERE seq = #{seq}
   AND user_id = #{userId}
