mapper.update:
        UPDATE D_COMMENT
		   SET mod_id = #{modId},
		       comments = #{comments},
		       mod_dt  = SYSDATE
		 WHERE comseq = #{comSeq}
		   AND seq = #{seq}
		   AND user_id = #{userId}

mapper.delete:
	   DELETE FROM D_COMMENT
        WHERE comseq = #{comSeq}
          AND user_id = #{userId}
